import React, {useEffect, useState} from 'react';


const onPageScroll = () => {

    useEffect(() => {
        window.addEventListener('scroll', scrollHandler);
        return() => {
            return window.removeEventListener('scroll', scrollHandler)
        }
    })

    const [inView, setInView] = useState(false)
 
    return <>
     <img src="/images/img-1.jpg" width="200" height="200"/>
     <br/>
     <img src="/images/img-2.jpg" width="200" height="200"/>
     <br/>
     <img src="/images/img-3.jpg" width="200" height="200"/>
     <br/>
     <img src="/images/img-4.jpg" width="200" height="200"/>
     <br/>
     <img src="/images/img-1.jpg" width="200" height="200"/>
