import React, { useState } from "react";

function HomePage() {
  
  const [inputState, setInputState] = useState({
    firstName: "",
    lastName: "",
  });

  const updateVal = (e) => {
    setInputState({
      [name]: value
    });
  };
  return (
    <>
      {/* <input type="text"
        onChange={(e) => setInputState(e.target.value)} /> */}
      <div>
        <input
          type="text"
          onChange={updateVal}
          value={inputState.firstName}
          name="firstName"
        />
        <span>First Name:{inputState.firstName}</span>
      </div>
      <div>
        <input
          type="text"
          onChange={updateVal}
          value={inputState.lastName}
          name="lastName"
        />
        <span>Last Name:{inputState.lastName}</span>
      </div>
    </>
  );
}

export default HomePage;
