import React, { useState } from "react";

function HomePage() {
  const [inputState, setInputState] = useState("")

  function updateVal(e){
     setInputState()
  }
  return <>
  {/* <input type="text"
        onChange={(e) => setInputState(e.target.value)} /> */}
       <div>
       <input type="text"
        onChange={updateVal} onClick={updateVal} value={inputState} />
        <span>First Name:{inputState}</span>
       </div>
       <div>
       <input type="text"
        onChange={updateVal} onClick={updateVal} value={inputState} />
        <span>Last Name:{inputState}</span>
       </div>
  </>;
}

export default HomePage;
