import React, { useState } from "react";

function HomePage() {
  const [inputState, setInputState] = useState({
    firstName:'',
    lastName:''
  })

  const updateVal = (e) => {
    setInputState(prevState => ({
      ...prevState,
      [e.target.name]:e.target.value
    }))
  }
  return <>
  {/* <input type="text"
        onChange={(e) => setInputState(e.target.value)} /> */}
       <div>
       <input type="text"
        onChange={updateVal} value={inputState.firstName} />
        <span>First Name:{inputState.firstName}</span>
       </div>
       <div>
       <input type="text"
        onChange={updateVal}  value={inputState.lastName} />
        <span>Last Name:{inputState.lastName}</span>
       </div>
  </>;
}

export default HomePage;
