import React, { useState } from "react";

function HomePage() {
  const [inputState, setInputState] = useState()
  const updateVal = (e) => {
     setInputState(prevState => ({
       [e.target.name]: e.target.value
     }))
  }
  return <>
  <input type="text"
        onChange={()=>updateVal} value={inputState} />
  </>;
}

export default HomePage;
