    const imgb = useRef(null)

   return (
      <>
        <img src={`${/images/img-1}.jpg`} onMouseOver = {() => {imga.current.src = "/images/img-2.jpg"}} ref={imga} width="100" height="100" />
       <img src='' onMouseOut = {() => imgb.current.imga} ref={imgb} width="100" height="100" />
      </>
   )
}

export default hoverOnImage
