import React, { useRef } from "react";

const hoverOnImage = () => {
  const imga = useRef(null);

  return (
    <>
      <img
        src="/images/img-1.jpg"
        onMouseOver={() => {
          imga.current.src = "/images/img-2.jpg";
        }}
        onMouseOut={() => {
          imga.current.src = "/images/img-1.jpg";
        }}
        ref={imga}
        width="100"
        height="100"
      />
    </>
  );
};

export default hoverOnImage;
